mapper.select:
SELECT * FROM audit_rule_tb
WHERE rule_id = #{ruleId}
  AND company_id = #{companyId}

SELECT * FROM audit_rule_tb
WHERE company_id = #{companyId}
ORDER BY rule_type, rule_name

mapper.insert:
INSERT INTO audit_rule_tb (
    company_id, rule_name, rule_type, rule_config, is_active
) VALUES (
    #{companyId}, #{ruleName}, #{ruleType}, 
    #{ruleConfigJson}, 
    #{isActive}
)

mapper.delete:
DELETE FROM audit_rule_tb
WHERE rule_id = #{ruleId}
  AND company_id = #{companyId}
