mapper.select:
SELECT * FROM company_tb
WHERE company_code = #{companyCode}
LIMIT 1

SELECT * FROM company_tb
WHERE company_id = #{companyId}
LIMIT 1

SELECT COUNT(*) > 0
FROM company_tb
WHERE company_code = #{companyCode}

mapper.update:
UPDATE company_tb
SET subscription_id = #{subscriptionId}
WHERE company_id = #{companyId}

UPDATE company_tb
SET is_active = #{isActive}
WHERE company_id = #{companyId}
