mapper.select:
SELECT 
    mc.*,
    u.korean_name AS closed_by_name
FROM monthly_closing_tb mc
LEFT JOIN user_tb u ON mc.closed_by = u.user_id
WHERE mc.company_id = #{companyId}
  AND mc.closing_year = #{year}
  AND mc.closing_month = #{month}

mapper.insert:
INSERT INTO monthly_closing_tb (
    company_id, closing_year, closing_month, closed_by, closed_at, is_closed
) VALUES (
    #{companyId}, #{closingYear}, #{closingMonth}, #{closedBy}, 
    #{closedAt}, #{isClosed}
)

mapper.update:
UPDATE monthly_closing_tb
SET
    is_closed = 0,
    updated_at = NOW()
WHERE closing_id = #{closingId}
  AND company_id = #{companyId}

mapper.select:
SELECT COUNT(*) > 0
FROM monthly_closing_tb
WHERE company_id = #{companyId}
  AND closing_year = #{year}
  AND closing_month = #{month}
  AND is_closed = 1
