mapper.select:
SELECT * FROM user_tb
WHERE role = #{role}
ORDER BY user_id

mapper.update:
UPDATE user_tb
SET is_active = false
WHERE user_id = #{userId}

UPDATE user_tb
SET password = #{password}
WHERE user_id = #{userId}

mapper.select:
SELECT * FROM user_tb
WHERE email = #{email}
LIMIT 1

SELECT COUNT(*) > 0
FROM user_tb
WHERE email = #{email}
