mapper.select:
SELECT * FROM user_tb
WHERE role = #{role}
AND company_id = #{companyId}
ORDER BY user_id

mapper.update:
UPDATE user_tb
SET is_active = false
WHERE user_id = #{userId}

UPDATE user_tb
SET password = #{password}
WHERE user_id = #{userId}

mapper.select:
SELECT * FROM user_tb
WHERE email = #{email}
LIMIT 1

SELECT COUNT(*) > 0
FROM user_tb
WHERE email = #{email}

    AND company_id = #{companyId}

SELECT COUNT(*) > 0
FROM user_tb
WHERE username = #{username}

SELECT uc.user_company_id AS userCompanyId, uc.user_id AS userId, uc.company_id AS companyId, uc.role, uc.position, uc.is_active AS isActive, uc.is_primary AS isPrimary, uc.approval_status AS approvalStatus, uc.created_at AS createdAt, uc.updated_at AS updatedAt, u.korean_name AS koreanName, u.username, u.email, c.company_name AS companyName, c.company_code AS companyCode FROM user_company_tb uc INNER JOIN user_tb u ON uc.user_id = u.user_id INNER JOIN company_tb c ON uc.company_id = c.company_id WHERE uc.user_id = #{userId}
AND uc.approval_status = #{status}
ORDER BY uc.is_primary DESC, uc.created_at DESC
