mapper.select:
SELECT *
FROM user_signature_tb
WHERE signature_id = #{signatureId}
  AND company_id = #{companyId}
LIMIT 1

SELECT *
FROM user_signature_tb
WHERE user_id = #{userId}
  AND company_id = #{companyId}
ORDER BY is_default DESC, created_at DESC

SELECT *
FROM user_signature_tb
WHERE user_id = #{userId}
  AND company_id = #{companyId}
  AND is_default = 1
LIMIT 1

mapper.delete:
DELETE FROM user_signature_tb
WHERE signature_id = #{signatureId}
  AND company_id = #{companyId}

mapper.update:
UPDATE user_signature_tb
SET is_default = 0,
    updated_at = NOW()
WHERE user_id = #{userId}
  AND company_id = #{companyId}
  AND is_default = 1
